INSERT INTO `leadings` VALUES (7, 'Bánh xốp mang hình kem ốc quế cực xinh', 'Thêm một gợi ý về một món quà độc đáo, nhanh gọn, dễ thực hiện cho teenboy nhà mình nhé!', '<p class="sapo">\r\n	&nbsp;</p>\r\n<div class="content">\r\n	<table border="0" cellpadding="0" cellspacing="0" width="100%">\r\n		<tbody>\r\n			<tr>\r\n				<td align="left" style="padding-left: 3px;" width="100%">\r\n					<div>\r\n						&nbsp;</div>\r\n					<div>\r\n						<span style="font-family: Arial; color: rgb(0, 0, 0); font-size: 12px; font-weight: bold; text-decoration: underline;">Chuẩn bị những nguy&ecirc;n liệu sau:</span></div>\r\n					<div>\r\n						&nbsp;</div>\r\n					<div>\r\n						&nbsp;</div>\r\n				</td>\r\n			</tr>\r\n			<tr>\r\n				<td align="left" width="100%">\r\n					<table border="0" cellpadding="0" cellspacing="0" width="100%">\r\n						<tbody>\r\n							<tr>\r\n								<td style="padding-left: 15px; font-family: Arial; color: rgb(51, 51, 51); font-size: 12px;" valign="top">\r\n									<table align="left" cellspacing="0">\r\n										<tbody>\r\n											<tr>\r\n												<td style="padding-right: 15px;" valign="top">\r\n													<div>\r\n														&nbsp;&nbsp; <img src="http://k14.vcmedia.vn/Images/Uploaded/Share/2011/3/7/120/110307mbtocquenguyenlieu12.jpg" style="border: 1px solid rgb(0, 0, 0);" /></div>\r\n													<div>\r\n														&nbsp;</div>\r\n													<div>\r\n														&nbsp;</div>\r\n												</td>\r\n											</tr>\r\n										</tbody>\r\n									</table>\r\n									<div align="justify">\r\n										<span style="font-style: italic; color: rgb(0, 0, 205); font-weight: bold;">- B&aacute;nh xốp</span></div>\r\n									<div align="justify">\r\n										<span style="font-style: italic; color: rgb(0, 0, 205); font-weight: bold;">&nbsp;</span></div>\r\n									<div align="justify">\r\n										<span style="font-style: italic; color: rgb(0, 0, 205); font-weight: bold;">- Kẹo marshmallow</span></div>\r\n									<div align="justify">\r\n										<span style="font-style: italic; color: rgb(0, 0, 205); font-weight: bold;">&nbsp;</span></div>\r\n									<div align="justify">\r\n										<span style="font-style: italic; color: rgb(0, 0, 205); font-weight: bold;">- Chocolate trắng</span></div>\r\n									<div align="justify">\r\n										<span style="font-style: italic; color: rgb(0, 0, 205); font-weight: bold;">&nbsp;</span></div>\r\n									<div align="justify">\r\n										<span style="font-style: italic; color: rgb(0, 0, 205); font-weight: bold;">- Đường m&agrave;u trang tr&iacute;</span></div>\r\n								</td>\r\n							</tr>\r\n						</tbody>\r\n					</table>\r\n				</td>\r\n			</tr>\r\n			<tr>\r\n				<td align="left" style="padding-left: 3px;" width="100%">\r\n					&nbsp;</td>\r\n			</tr>\r\n			<tr>\r\n				<td align="left" width="100%">\r\n					<table border="0" cellpadding="0" cellspacing="0" width="100%">\r\n						<tbody>\r\n						</tbody>\r\n					</table>\r\n				</td>\r\n			</tr>\r\n			<tr>\r\n				<td align="left" style="padding-left: 3px;" width="100%">\r\n					<div>\r\n						<span style="font-family: Arial; color: rgb(0, 0, 0); font-size: 12px; font-weight: bold; text-decoration: underline;">Đến phần h&agrave;nh động n&agrave;y: &gt;:D&lt;</span></div>\r\n					<div>\r\n						&nbsp;</div>\r\n					<div>\r\n						&nbsp;</div>\r\n				</td>\r\n			</tr>\r\n			<tr>\r\n				<td align="left" width="100%">\r\n					<table border="0" cellpadding="0" cellspacing="10" width="100%">\r\n						<tbody>\r\n							<tr>\r\n								<td style="padding-left: 15px; font-family: Arial; color: rgb(51, 51, 51); font-size: 12px;" valign="top">\r\n									<table align="left" cellspacing="0">\r\n										<tbody>\r\n											<tr>\r\n												<td style="padding-right: 15px;" valign="top">\r\n													<div>\r\n														<img src="http://k14.vcmedia.vn/Images/Uploaded/Share/2011/3/7/120/110307mbtocque12.jpg" style="border: 1px solid rgb(0, 0, 0);" /></div>\r\n													<div>\r\n														&nbsp;</div>\r\n													<div>\r\n														&nbsp;</div>\r\n												</td>\r\n											</tr>\r\n										</tbody>\r\n									</table>\r\n									<div>\r\n										<span style="font-weight: bold; text-decoration: underline;">Bước 1:</span></div>\r\n									<div>\r\n										&nbsp;</div>\r\n									<div align="justify">\r\n										<span style="font-style: italic;">- Đầu ti&ecirc;n, c&aacute;c bạn cắt b&aacute;nh xốp th&agrave;nh dạng que nhỏ nh&eacute;!</span></div>\r\n								</td>\r\n							</tr>\r\n							<tr>\r\n								<td style="padding-left: 15px; font-family: Arial; color: rgb(51, 51, 51); font-size: 12px;" valign="top">\r\n									<table align="left" cellspacing="0">\r\n										<tbody>\r\n											<tr>\r\n												<td style="padding-right: 15px;" valign="top">\r\n													<div>\r\n														<img src="http://k14.vcmedia.vn/Images/Uploaded/Share/2011/3/7/120/110307mbtocque22.jpg" style="border: 1px solid rgb(0, 0, 0);" /></div>\r\n													<div>\r\n														&nbsp;</div>\r\n													<div>\r\n														&nbsp;</div>\r\n												</td>\r\n											</tr>\r\n										</tbody>\r\n									</table>\r\n									<div>\r\n										<span style="font-weight: bold; text-decoration: underline;">Bước 2:</span></div>\r\n									<div>\r\n										&nbsp;</div>\r\n									<div align="justify">\r\n										<span style="font-style: italic;">- Cắt một miếng marshmallow nhỏ rồi đục một lỗ ở giữa v&agrave; lồng v&agrave;o b&aacute;nh xốp n&agrave;y.</span></div>\r\n								</td>\r\n							</tr>\r\n							<tr>\r\n								<td style="padding-left: 15px; font-family: Arial; color: rgb(51, 51, 51); font-size: 12px;" valign="top">\r\n									<table align="left" cellspacing="0">\r\n										<tbody>\r\n											<tr>\r\n												<td style="padding-right: 15px;" valign="top">\r\n													<div>\r\n														<img src="http://k14.vcmedia.vn/Images/Uploaded/Share/2011/3/7/120/110307mbtocque32.jpg" style="border: 1px solid rgb(0, 0, 0);" /></div>\r\n													<div>\r\n														&nbsp;</div>\r\n													<div>\r\n														&nbsp;</div>\r\n												</td>\r\n											</tr>\r\n										</tbody>\r\n									</table>\r\n									<div>\r\n										<span style="font-weight: bold; text-decoration: underline;">Bước 3:</span></div>\r\n									<div>\r\n										&nbsp;</div>\r\n									<div align="justify">\r\n										<span style="font-style: italic;">- Tiếp theo, c&aacute;c bạn l&agrave;m th&ecirc;m một tầng marshmallow ở tr&ecirc;n nữa nghen.</span></div>\r\n								</td>\r\n							</tr>\r\n							<tr>\r\n								<td style="padding-left: 15px; font-family: Arial; color: rgb(51, 51, 51); font-size: 12px;" valign="top">\r\n									<table align="left" cellspacing="0">\r\n										<tbody>\r\n											<tr>\r\n												<td style="padding-right: 15px;" valign="top">\r\n													<div>\r\n														<img src="http://k14.vcmedia.vn/Images/Uploaded/Share/2011/3/7/120/110307mbtocque42.jpg" style="border: 1px solid rgb(0, 0, 0);" /></div>\r\n													<div>\r\n														&nbsp;</div>\r\n													<div>\r\n														&nbsp;</div>\r\n												</td>\r\n											</tr>\r\n										</tbody>\r\n									</table>\r\n									<div>\r\n										<span style="font-weight: bold; text-decoration: underline;">Bước 4:</span></div>\r\n									<div>\r\n										&nbsp;</div>\r\n									<div align="justify">\r\n										<span style="font-style: italic;">- Đun chảy chocolate trắng. Sau đ&oacute;, nh&uacute;ng đầu kem ốc quế v&agrave;o r&ugrave;i rắc đường m&agrave;u l&ecirc;n l&agrave; xong</span>!</div>\r\n								</td>\r\n							</tr>\r\n							<tr>\r\n								<td style="padding-left: 15px; font-family: Arial; color: rgb(51, 51, 51); font-size: 12px;" valign="top">\r\n									<table align="left" cellspacing="0">\r\n										<tbody>\r\n											<tr>\r\n												<td style="padding-right: 15px;" valign="top">\r\n													<div>\r\n														<img src="http://k14.vcmedia.vn/Images/Uploaded/Share/2011/3/7/120/110307mbtocque52.jpg" style="border: 1px solid rgb(0, 0, 0);" /></div>\r\n													<div>\r\n														&nbsp;</div>\r\n												</td>\r\n											</tr>\r\n										</tbody>\r\n									</table>\r\n									<div>\r\n										<span style="font-weight: bold; text-decoration: underline;">Bước 5:</span></div>\r\n									<div>\r\n										&nbsp;</div>\r\n									<div align="justify">\r\n										<span style="font-style: italic;">- Cuối c&ugrave;ng, ch&uacute;ng m&igrave;nh cần l&agrave;m th&ecirc;m một chiếc hộp nhỏ để cho b&aacute;nh v&agrave;o nữa l&agrave; xong!</span></div>\r\n								</td>\r\n							</tr>\r\n						</tbody>\r\n					</table>\r\n				</td>\r\n			</tr>\r\n			<tr>\r\n				<td align="left" style="padding-left: 3px;" width="100%">\r\n					<div>\r\n						<span style="font-family: Arial; color: rgb(0, 0, 0); font-size: 12px; font-weight: bold;">Gợi &yacute; mẫu hộp cho c&aacute;c bạn n&egrave;... </span></div>\r\n					<div>\r\n						&nbsp;</div>\r\n					<div>\r\n						&nbsp;</div>\r\n				</td>\r\n			</tr>\r\n			<tr>\r\n				<td align="center" style="padding-left: 3px;" width="100%">\r\n					<div align="center">\r\n						<img _fl="" align="middle" src="http://k14.vcmedia.vn/Images/Uploaded/Share/2011/03/07/110307MBTocquesp1.jpg" style="margin: 5px;" width="500" /></div>\r\n					<div align="center">\r\n						<span style="font-style: italic;">Đơn giản m&agrave; cực&nbsp;dễ thương nh&aacute;!!! </span></div>\r\n					<div align="center">\r\n						&nbsp;</div>\r\n					<div align="center">\r\n						&nbsp;</div>\r\n					<div align="center">\r\n						<div>\r\n							<img _fl="" align="middle" src="http://k14.vcmedia.vn/Images/Uploaded/Share/2011/03/07/110307MBTocquesp2.jpg" style="margin: 5px;" width="500" /></div>\r\n					</div>\r\n				</td>\r\n			</tr>\r\n		</tbody>\r\n	</table>\r\n</div>\r\n<p>\r\n	&nbsp;</p>\r\n', '110307MBTocquesp2.jpg', NULL, 1);
