INSERT INTO `promotion` VALUES (6, 'Chào mừng Quốc Tế Phụ Nữ', 'Chúng mình tiếp tục cập nhật những xì tai đón mùa đông trên thế giới nhé!', 'boeum1280910846.jpg', '<p>\r\n	Nh&acirc;n dịp ch&agrave;o mừng ng&agrave;y Quốc Tế Phụ Nữ mừng 8-3. Cửa hiệu ch&uacute;ng tối giảm gi&aacute; 10% cho tất cả c&aacute;c mặt h&agrave;ng. Miễn ph&iacute; tiền vận chuyển sản phẩm đối với c&aacute;c địa chỉ nằm trong b&aacute;n k&iacute;nh 10km từ trung t&acirc;m th&agrave;nh phố H&agrave; Nội</p>\r\n', 1);
INSERT INTO `promotion` VALUES (7, 'Khuyến mãi hàng tháng', 'Chúng mình tiếp tục cập nhật những xì tai đón mùa đông trên thế giới nhé!', '1.png', '<p>\r\n	<img alt="" src="http://localhost:8888/Project-C1007M-Nhom2/upload/data/user1208939_pic936819_1298685811.jpg " style="width: 220px; height: 220px; float: left;" /> V&agrave;o mỗi ng&agrave;y 17 h&agrave;ng th&aacute;ng, website giảm gi&aacute; 20% cho tất cả c&aacute;c h&oacute;a đơn c&oacute; mệnh gi&aacute; thanh to&aacute;n lớn hơn 500.000 VND. Đối với c&aacute;c h&oacute;a đơn c&oacute; mệnh gi&aacute; từ 100.000- 500.000 sẽ được tặng th&ecirc;m một sản phẩm ngẫu nhi&ecirc;n.</p>\r\n', 1);
